import classes from "./Home.module.css";
import Header from "../../components/header/Header";
import Card from "../../UI/card/Card";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { Fetchaction, clearItem } from "../../redux/actions/Fetchaction";
import Loader from "../../UI/loader/Loader";

const Home = () => {
  const [value, setValue] = useState();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(Fetchaction("politics"));
  }, [dispatch]);

  const homedata = useSelector((state) => state.Homeslice.item);
  const Loading = useSelector((state) => state.Homeslice.isLoading);
  console.log(homedata);
  const newhomedata = homedata?.[0]?.articles?.slice(0, 20);

  const handleSearch = () => {
    if (value.match(/[0-9]/)) {
      alert("Please enter a valid city name. Integers are not allowed.");
    } else {
      dispatch(clearItem());
      dispatch(Fetchaction(value));
    }
  };

  return (
    <>
      {!Loading && (
        <div className={classes.homecontainer}>
          <div>
            <Header />
          </div>
          <div className={classes.homeinput}>
            <input
              type="text"
              placeholder="Search Here"
              onChange={(e) => {
                const inputValue = e.target.value;
                if (!/\d/.test(inputValue)) {
                  setValue(inputValue);
                } else {
                  alert(
                    "Please enter a valid city name. Integers are not allowed."
                  );
                }
              }}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
          <div className={classes.homeheading}>
            <h2>Latest News</h2>
          </div>
          <div className={classes.homecards}>
            {newhomedata?.map((i) => (
              <Card
                key={Math.random()}
                title={i.title}
                description={i.description}
                publish={i.publishedAt}
                img={i.urlToImage}
                readmore={i.url}
              />
            ))}
          </div>
        </div>
      )}
      {Loading && <Loader />}
    </>
  );
};

export default Home;
