import classes from "./Home.module.css";
import Header from "../../components/header/Header";
import Card from "../../UI/Card";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { Fetchaction } from "../../redux/actions/Fetchaction";

const Home = () => {
  const [value, setValue] = useState("politics");
  const dispatch = useDispatch();

  // Fetch data initially when the component mounts
  useEffect(() => {
    dispatch(Fetchaction(value));
  }, [dispatch, value]);

  // Get the latest data from the Redux store
  const homedata = useSelector((state) => state.Homeslice.item);
  const newhomedata = homedata?.[0]?.articles?.slice(0, 20);

  // Function to handle search button click
  const handleSearch = () => {
    dispatch(Fetchaction(value));
  };

  return (
    <div className={classes.homecontainer}>
      <div>
        <Header />
      </div>
      <div className={classes.homeinput}>
        <input
          type="text"
          placeholder="Search Here"
          onChange={(e) => setValue(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className={classes.homeheading}>
        <h2>Latest News</h2>
      </div>
      <div className={classes.homecards}>
        {newhomedata?.map((i) => (
          <Card
            key={i.title}
            title={i.title}
            description={i.description}
            publish={i.publishedAt}
            img={i.urlToImage}
          />
        ))}
      </div>
    </div>
  );
};

export default Home;
