import classes from "./Sports.module.css";
import Header from "../../components/header/Header";
import Story from "../../components/story/Story";
import { useEffect } from "react";
import { Fetchaction } from "../../redux/actions/Fetchaction";
import { useDispatch, useSelector  } from "react-redux";
import { clearItem } from "../../redux/actions/Fetchaction";

const Sports = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(clearItem());
    dispatch(Fetchaction("sports"));
  }, [dispatch]);
  const homedata = useSelector((state) => state.Homeslice.item);
  const newhomedata = homedata?.[0]?.articles?.slice(0, 5);
  return (
    <div className={classes.sportscontainer}>
      <div>
        <Header />
      </div>
      <div className={classes.sportsheading}>
        <h1>Top Stories</h1>
      </div>
      <div className={classes.sportsstory}>
      {newhomedata?.map((i) => (
              <Story
                key={Math.random()}
                title={i.title}
                img={i.urlToImage}
                readmore={i.url}
              />
            ))}
      </div>
    </div>
  );
};
export default Sports;
