import classes from "./Home.module.css";
import Header from "../../components/header/Header";
import Card from "../../UI/Card";
import { useDispatch } from "react-redux";
import { Fetchaction } from "../../redux/actions/Fetchaction";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const Home = () => {
  const [value,setValue]=useState('politics');
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(Fetchaction(value));
  }, [dispatch,value]);

  const homedata = useSelector((state) => state.Homeslice.item);
  const newhomedata = homedata?.[0]?.articles?.slice(0, 20);

  return (
    <div className={classes.homecontainer}>
      <div>
        <Header />
      </div>
      <div className={classes.homeinput}>
        <input type="text" placeholder="Search Here" onChange={(e)=>setValue(e.target.value)} />
        <button>Search</button>
      </div>
      <div className={classes.homeheading}>
        <h2>Latest News</h2>
      </div>
      <div className={classes.homecards}>
        {newhomedata?.map((i)=>{
          return(
            <Card key={Math.round()} title={i.title} description={i.description} publish={i.publishedAt} img={i.urlToImage} />
          )
        })}
      </div>
    </div>
  );
};
export default Home;
