import classes from "./Sports.module.css";
import Header from "../../components/header/Header";
import Story from "../../components/story/Story";
import { useEffect } from "react";
import { Fetchaction } from "../../redux/actions/Fetchaction";
import { useDispatch, useSelector } from "react-redux";
import { clearItem } from "../../redux/actions/Fetchaction";
import Loader from "../../UI/loader/Loader";
import Explore from "../../components/explore/Explore";

const Sports = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(clearItem());
    dispatch(Fetchaction("sports"));
  }, [dispatch]);
  const data = useSelector((state) => state.Homeslice.item);
  const newstorydata = data?.[0]?.articles?.slice(0, 6);
  const newstorydata1 = data?.[0]?.articles?.slice(16, 20);
  const newexploredata = data?.[0]?.articles?.[15];
  const Loading = useSelector((state) => state.Homeslice.isLoading);
  return (
    <>
      {!Loading && (
        <div className={classes.sportscontainer}>
          <div>
            <Header />
          </div>
          <div className={classes.sportsheading}>
            <h1>Top Stories</h1>
          </div>
          <div className={classes.sportsstory}>
            {newstorydata?.map((i) => (
              <Story
                key={Math.random()}
                title={i.title}
                img={i.urlToImage}
                readmore={i.url}
              />
            ))}
          </div>
          <div className={classes.sportsheading}>
            <h1>More to explore</h1>
          </div>
          <div className={classes.sportsexplore}>
              <Explore
                key={Math.random()}
                description={newexploredata?.description}
                img={newexploredata?.urlToImage}
                readmore={newexploredata?.url}
              />
              <div className={classes.sportsexplore1}>
              {newstorydata1?.map((i) => (
              <Story
                key={Math.random()}
                title={i.title}
                img={i.urlToImage}
                readmore={i.url}
              />
            ))}
              </div>
          </div>
        </div>
      )}
      {Loading && <Loader/> }
    </>
  );
};
export default Sports;
